#' @rdname parameters
#' @export
weight_decay <-
  new_quant_param(
    type = "double",
    range = c(-10, 0),
    inclusive = c(TRUE, TRUE),
    trans = log10_trans(),
    label = "L2 Regularization"
  )

#' @rdname parameters
