# in reference to kknn::kknn
#' @rdname parameters
#' @export
weight_func <-
  new_qual_param(
    type = "character",
    values = c("rectangular", "triangular", "epanechnikov", "biweight", 
               "triweight", "cos", "inv", "gaussian", "rank", "optimal"),
    label = "Distance Weighting Function"
  )

