#' Parameter Objects
#' 
#' These are objects that can be used for modeling, especially in conjunction
#'  with the \pkg{parsnip} package.  
#' 
#' @return Each object is generated by either `new_quant_param` or 
#' `new_qual_param`. 
#' 
#' @export
#' @rdname parameters
mtry <-
  new_quant_param(
    type = "integer",
    range = c(1L, unknown()),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
trees <-
  new_quant_param(
    type = "integer",
    range = c(1L, 2000L),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
min_n <-
  new_quant_param(
    type = "integer",
    range = c(2L, unknown()),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
sample_size <-
  new_quant_param(
    type = "integer",
    range = c(unknown(), unknown()),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
learn_rate <-
  new_quant_param(
    type = "double",
    range = c(unknown(), unknown()),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )


#' @rdname parameters
#' @export
loss_reduction <-
  new_quant_param(
    type = "double",
    range = c(unknown(), unknown()),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
tree_depth <-
  new_quant_param(
    type = "integer",
    range = c(2L, 15L),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )


#' @rdname parameters
#' @export
weight_decay <-
  new_quant_param(
    type = "double",
    range = c(-10, 0),
    inclusive = c(TRUE, TRUE),
    trans = log10_trans()
  )

#' @rdname parameters
#' @export
dropout <-
  new_quant_param(
    type = "double",
    range = c(0, 1),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
epochs <-
  new_quant_param(
    type = "integer",
    range = c(1L, 1000L),
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
activation <-
  new_qual_param(
    type = "character",
    values = c("linear", "softmax", "relu", "elu")
  )

#' @rdname parameters
#' @export
mixture <- 
  new_quant_param(
    type = "double",
    range = 0:1,
    inclusive = c(TRUE, TRUE),
    trans = NULL
  )

#' @rdname parameters
#' @export
regularization <- 
  new_quant_param(
    type = "double",
    range = c(-10, 0),
    inclusive = c(TRUE, TRUE),
    trans = log10_trans()
  )

# in reference to kknn::kknn
#' @rdname parameters
#' @export
weight_func <-
  new_qual_param(
    type = "character",
    values = c("rectangular", "triangular", "epanechnikov", "biweight", 
               "triweight", "cos", "inv", "gaussian", "rank", "optimal")
  )

# in reference to survival::survreg
#' @rdname parameters
#' @export
surv_dist <-
  new_qual_param(
    type = "character",
    values = c("weibull", "exponential", "gaussian", "logistic", 
               "lognormal", "loglogistic")
  )

#' @export
#' @rdname parameters
prune <-
  new_qual_param(
    type = "logical",
    values = c(TRUE, FALSE)
  )
